//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Hotel {
  address payable public owner;
  uint public roomPrice;
  uint public roomAvailability;
  
  event RoomBooked(address indexed _from, uint _price, uint _availability);
  event RoomAvailabilitySet(address indexed _from, uint _availability);

  constructor(uint _roomPrice, uint _roomAvailability) {
    owner = payable(msg.sender);
    roomPrice = _roomPrice;
    roomAvailability = _roomAvailability;
  }
  
  function bookRoom() payable public {
    require(msg.value == roomPrice, "Invalid amount");
    require(roomAvailability > 0, "No rooms available");
    
    roomAvailability--;
    owner.transfer(msg.value);

    emit RoomBooked(msg.sender, roomPrice, roomAvailability);
  }
  
  function getRoomAvailability() public view returns (uint) {
    return roomAvailability;
  }
  
  function setRoomAvailability(uint _roomAvailability) public {
    require(msg.sender == owner, "Only owner can set room availability");
    roomAvailability = _roomAvailability;

    emit RoomAvailabilitySet(msg.sender, roomAvailability);
  }
}
